import { addTeacher } from "./api.js";
import dict from "@/views/template/dict";
import uploadPicInput from "@/views/template/upload-pic-input";
import uploadFileInput from "@/views/template/upload-file-input";
export default {
  name: "add",
  components: {
    uploadPicInput,
    uploadFileInput,
    dict
  },
  data() {
    return {
      submitLoading: false,
      // 表单提交状态
      form: {
        // 添加或编辑表单对象初始化数据
        name: "",
        education: "",
        age: 0,
        graduated: "",
        wages: 0,
        status: "",
        autograph: "",
        resume: "",
        remark: ""
      },
      // 表单验证规则
      formValidate: {}
    };
  },
  methods: {
    init() {},
    handleReset() {
      this.$refs.form.resetFields();
    },
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          addTeacher(this.form).then(res => {
            this.submitLoading = false;
            if (res.success) {
              this.$Message.success("操作成功");
              this.submited();
            }
          });
        }
      });
    },
    close() {
      this.$emit("close", true);
    },
    submited() {
      this.$emit("submited", true);
    }
  },
  mounted() {
    this.init();
  }
};
