import { editStudent } from "./api.js";
import dict from "@/views/template/dict";
export default {
  name: "edit",
  components: {
    dict
  },
  props: {
    data: Object
  },
  data() {
    return {
      submitLoading: false,
      // 表单提交状态
      form: {
        // 添加或编辑表单对象初始化数据
        name: "",
        sex: "",
        age: 0,
        number: "",
        school: ""
      },
      // 表单验证规则
      formValidate: {}
    };
  },
  methods: {
    init() {
      this.handleReset();
      this.form = this.data;
    },
    handleReset() {
      this.$refs.form.resetFields();
    },
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          editStudent(this.form).then(res => {
            this.submitLoading = false;
            if (res.success) {
              this.$Message.success("操作成功");
              this.submited();
            }
          });
        }
      });
    },
    close() {
      this.$emit("close", true);
    },
    submited() {
      this.$emit("submited", true);
    }
  },
  mounted() {
    this.init();
  }
};
