import { editEmploymentStatus } from "./api.js";
import uploadPicInput from "@/views/template/upload-pic-input";
export default {
  name: "edit",
  components: {
    uploadPicInput
  },
  props: {
    data: Object
  },
  data() {
    return {
      submitLoading: false,
      // 表单提交状态
      form: {
        // 添加或编辑表单对象初始化数据
        userId: "",
        userName: "",
        userNumber: "",
        userMobile: "",
        status: ""
      },
      // 表单验证规则
      formValidate: {}
    };
  },
  methods: {
    init() {
      this.handleReset();
      this.form = this.data;
      this.form.status = this.form.status + "";
      this.tempDate = this.form.date;
      var array = this.form.county.split(",");
      this.form.county = array;
    },
    handleReset() {
      this.$refs.form.resetFields();
    },
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          editEmploymentStatus(this.form).then(res => {
            this.submitLoading = false;
            if (res.success) {
              this.$Message.success("操作成功");
              this.submited();
            }
          });
        }
      });
    },
    close() {
      this.$emit("close", true);
    },
    submited() {
      this.$emit("submited", true);
    }
  },
  mounted() {
    this.init();
  }
};
