import { getUserListData } from "@/api/index";
import departmentChoose from "./department-choose";
export default {
  name: "userChoose",
  components: {
    departmentChoose
  },
  props: {
    text: {
      type: String,
      default: "选择用户"
    },
    icon: {
      type: String,
      default: "md-person-add"
    }
  },
  data() {
    return {
      userLoading: true,
      userModalVisible: false,
      selectUsers: [],
      searchUserForm: {
        id: "",
        nickname: "",
        type: "",
        status: "",
        pageNumber: 1,
        // 当前页数
        pageSize: 10,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc" // 默认排序方式
      },

      userColumns: [{
        type: "index",
        width: 60,
        align: "center"
      }, {
        title: "用户名",
        key: "nickname",
        minWidth: 130,
        sortable: true
      }, {
        title: "登录账号",
        key: "username",
        minWidth: 130,
        sortable: true
      }, {
        title: "头像",
        key: "avatar",
        width: 80,
        align: "center",
        render: (h, params) => {
          return h("Avatar", {
            props: {
              src: params.row.avatar
            }
          });
        }
      }, {
        title: "所属部门",
        key: "departmentTitle",
        width: 120
      }, {
        title: "手机",
        key: "mobile",
        width: 125,
        sortable: true
      }, {
        title: "邮箱",
        key: "email",
        width: 180,
        sortable: true
      }, {
        title: "性别",
        key: "sex",
        width: 70,
        align: "center"
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        width: 170
      }, {
        title: "操作",
        key: "action",
        width: 130,
        align: "center",
        fixed: "right",
        render: (h, params) => {
          return h("div", [h("Button", {
            props: {
              type: "info",
              size: "small"
            },
            on: {
              click: () => {
                this.chooseUser(params.row);
              }
            }
          }, "添加该用户")]);
        }
      }],
      userData: [],
      totalUser: 0
    };
  },
  methods: {
    handleSelectDep(v) {
      this.searchUserForm.departmentId = v;
    },
    changeUserPage(v) {
      this.searchUserForm.pageNumber = v;
      this.getUserDataList();
    },
    changeUserPageSize(v) {
      this.searchUserForm.pageSize = v;
      this.getUserDataList();
    },
    getUserDataList() {
      this.userLoading = true;
      getUserListData(this.searchUserForm).then(res => {
        this.userLoading = false;
        if (res.success) {
          this.userData = res.result.records;
          this.totalUser = res.result.total;
        }
      });
    },
    handleSearchUser() {
      this.searchUserForm.pageNumber = 1;
      this.searchUserForm.pageSize = 10;
      this.getUserDataList();
    },
    handleResetUser() {
      this.$refs.searchUserForm.resetFields();
      this.searchUserForm.pageNumber = 1;
      this.searchUserForm.pageSize = 10;
      this.$refs.dep.clearSelect();
      this.searchUserForm.departmentId = "";
      // 重新加载数据
      this.getUserDataList();
    },
    setData(v) {
      this.selectUsers = v;
      this.$emit("on-change", this.selectUsers);
    },
    chooseUser(v) {
      // 去重
      let that = this;
      let flag = true;
      this.selectUsers.forEach(e => {
        if (v.id == e.id) {
          that.$Message.warning("已经添加过啦，请勿重复选择");
          flag = false;
        }
      });
      if (flag) {
        let u = {
          id: v.id,
          username: v.username,
          nickname: v.nickname
        };
        this.selectUsers.push(u);
        this.$emit("on-change", this.selectUsers);
        this.$Message.success(`添加用户 ${v.nickname} 成功`);
      }
    },
    clearSelectData() {
      this.selectUsers = [];
      this.$emit("on-change", this.selectUsers);
    },
    handleCancelUser(e, id) {
      // 删除所选用户
      this.selectUsers = this.selectUsers.filter(e => {
        return e.id != id;
      });
      this.$emit("on-change", this.selectUsers);
      this.$Message.success("删除所选用户成功");
    }
  },
  mounted() {
    this.getUserDataList();
  }
};
